<template>
    <div>
        <h2 class="header">Log in to your account</h2>
        <div class="auth0-form">
            <alert-box></alert-box>
            <form @submit.prevent="handleSubmit">
                <div class="form-group">
                    <label for="email">Email</label>
                    <input type="email" v-model="email" name="email" class="form-control" :class="{ 'is-invalid': submitted && !email }" />
                    <div v-show="submitted && !email" class="invalid-feedback">Email is required</div>
                </div>
                <div class="form-group">
                    <label htmlFor="password">Password</label>
                    <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" />
                    <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
                </div>
                <div class="form-group">
                    <button class="btn btn-primary btn-block" :disabled="status.loggingIn">Login</button>
                    <img v-show="status.loggingIn" src="data:image/gif;base64," />
                </div>
            </form>
        </div>
        <div class="white-text-center">
            <router-link to="/login/resetpassword" class="white-text-center btn btn-link">Can't log in?</router-link>
            <br/>
            <router-link to="/register" class="white-text-center btn btn-link">Sign up for an account</router-link>
        </div>
    </div>
</template>

<script>
import { mapState, mapActions } from 'vuex'
import alertBox from '../component/Alert';

export default {
    data () {
        return {
            email: '',
            password: '',
            submitted: false
        }
    },
    computed: {
        ...mapState('account', ['status'])
    },
    created () {
        // reset login status
        this.logout();
    },
    methods: {
        ...mapActions('account', ['login', 'logout']),
        handleSubmit (e) {
            this.submitted = true;
            const { email, password } = this;
            if (email && password) {
                this.login({ email, password })
            }
        }
    },
    components: {'alert-box': alertBox}
};
</script>